import React, { useState } from 'react';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import CircularProgress from '@mui/material/CircularProgress';
import Alert from '@mui/material/Alert';
import Button from '@mui/material/Button';

import SearchBar from '../components/SearchBar';
import UserList from '../components/UserList';
import RepoList from '../components/RepoList';

import { GitHubUser, GitHubRepo, searchUsers, getUserRepos } from '../services/githubApi';
import { useQuery, useInfiniteQuery } from '@tanstack/react-query';

const PER_PAGE = 10;

const HomePage: React.FC = () => {
  // Step: 1 = input search, 2 = list user, 3 = list repos
  const [step, setStep] = useState<number>(1);

  // state untuk input pencarian
  const [searchQuery, setSearchQuery] = useState<string>('');
  // state untuk trigger pencarian (agar pencarian manual saat tombol Search ditekan)
  const [submittedQuery, setSubmittedQuery] = useState<string>('');

  // useQuery untuk search users (step 2)
  const {
    data: users = [],
    isLoading: isLoadingUsers,
    error: userError,
    refetch: refetchUsers,
  } = useQuery<GitHubUser[], Error>({
    queryKey: ['searchUsers', submittedQuery],
    queryFn: () => searchUsers(submittedQuery),
    enabled: submittedQuery.trim().length > 0,
    staleTime: 1000 * 60 * 5,
  });

  // useInfiniteQuery untuk repos (step 3)
  const {
    data: repoData,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    error: repoError,
  } = useInfiniteQuery<GitHubRepo[], Error, { pages: GitHubRepo[]; pageParams: number[] }, any, number>({
    queryKey: ['userRepos', selectedUser?.login],
    initialPageParam: 1,
    queryFn: ({ pageParam = 1 }) => {
      if (!selectedUser) return Promise.resolve([]);
      return getUserRepos(selectedUser.login, pageParam, PER_PAGE);
    },
    enabled: !!selectedUser,
    getNextPageParam: (lastPage, allPages) => {
      return lastPage.length === PER_PAGE ? allPages.length + 1 : undefined;
    },
    staleTime: 1000 * 60 * 5,
  });

  // state untuk user yang dipilih
  const [selectedUser, setSelectedUser] = useState<GitHubUser | null>(null);

  // Handler Search (step 1)
  const handleSearch = () => {
    if (searchQuery.trim() === '') return;
    setSubmittedQuery(searchQuery);
    // Setelah pencarian, jika ada hasil, pindah ke step 2.
    // Karena useQuery akan melakukan fetch, kita tunggu efeknya melalui rendering.
    setStep(2);
    refetchUsers();
  };

  // Handler saat user dipilih (step 2)
  const handleSelectUser = (user: GitHubUser) => {
    setSelectedUser(user);
    setStep(3);
  };

  // Handler Load More repos (step 3)
  const handleLoadMore = () => {
    fetchNextPage();
  };

  // Ambil semua repos dari infinite query
  const repos: GitHubRepo[] = repoData ? repoData.pages.flat() : [];

  return (
    <Container maxWidth="sm" sx={{ paddingY: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        GitHub User Search
      </Typography>

      {step === 1 && (
        <div>
          <SearchBar searchQuery={searchQuery} onQueryChange={setSearchQuery} />
          <Button variant="contained" onClick={handleSearch} fullWidth>
            Search
          </Button>
          {isLoadingUsers && <CircularProgress sx={{ display: 'block', margin: '20px auto' }} />}
          {userError && <Alert severity="error">{userError.message}</Alert>}
        </div>
      )}

      {step === 2 && (
        <div>
          <Typography variant="h6" gutterBottom>
            Showing users for "{submittedQuery}"
          </Typography>
          {isLoadingUsers && <CircularProgress sx={{ display: 'block', margin: '20px auto' }} />}
          {userError && <Alert severity="error">{userError.message}</Alert>}
          {users.length > 0 ? (
            <UserList users={users} onSelectUser={handleSelectUser} />
          ) : (
            <Typography>No users found.</Typography>
          )}
          {/* Jika ingin menambahkan tombol "Back" untuk kembali ke step 1 */}
          <Button variant="text" onClick={() => setStep(1)} sx={{ marginTop: 2 }}>
            Back
          </Button>
        </div>
      )}

      {step === 3 && selectedUser && (
        <div>
          <RepoList
            repos={repos}
            username={selectedUser.login}
            onLoadMore={handleLoadMore}
            hasMore={!!hasNextPage}
            isLoadingMore={isFetchingNextPage}
          />
          {/* Jika ingin menambahkan tombol "Back" untuk kembali ke step 2 */}
          <Button variant="text" onClick={() => setStep(2)} sx={{ marginTop: 2 }}>
            Back
          </Button>
          {repoError && <Alert severity="error">{repoError.message}</Alert>}
        </div>
      )}
    </Container>
  );
};

export default HomePage;
