import React, { useState } from 'react';
import { Container, Typography, Box, CircularProgress, Alert, Button, Paper } from '@mui/material';

import SearchBar from '../components/SearchBar';
import UserList from '../components/UserList';
import { GitHubUser, searchUsers } from '../services/githubApi';
import { useQuery } from '@tanstack/react-query';

const HomePage: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [submittedQuery, setSubmittedQuery] = useState('');

  // Query untuk mencari user
  const { data: users = [], isLoading, error, refetch } = useQuery<GitHubUser[], Error>({
    queryKey: ['searchUsers', submittedQuery],
    queryFn: () => searchUsers(submittedQuery),
    enabled: false, // tidak berjalan otomatis, manual refetch
    staleTime: 1000 * 60 * 5,
  });

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;
    setSubmittedQuery(searchQuery.trim());
    await refetch();
  };

  return (
    <Container maxWidth="sm" sx={{ py: 4 }}>
      {/* Judul dengan tampilan modern */}
      <Box
        sx={{
          background: 'linear-gradient(135deg, #1976d2, #42a5f5)',
          borderRadius: 2,
          p: 3,
          mb: 4,
          textAlign: 'center',
          boxShadow: '0px 4px 10px rgba(0,0,0,0.2)',
        }}
      >
        <Typography
          variant="h4"
          component="h1"
          sx={{ fontWeight: 'bold', color: 'white', letterSpacing: 1 }}
        >
          GitHub User Search
        </Typography>
      </Box>

      {/* Bagian pencarian dalam Paper untuk tampilan modern */}
      <Paper
        sx={{
          p: 3,
          mb: 3,
          borderRadius: 2,
          boxShadow: 3,
          backgroundColor: 'background.paper',
        }}
      >
        <SearchBar searchQuery={searchQuery} onQueryChange={setSearchQuery} />
        <Button
          variant="contained"
          onClick={handleSearch}
          fullWidth
          sx={{ mt: 2, textTransform: 'none' }}
        >
          Search
        </Button>
      </Paper>

      {isLoading && <CircularProgress sx={{ display: 'block', mx: 'auto', my: 2 }} />}
      {error && <Alert severity="error">{error.message}</Alert>}

      {users.length > 0 && <UserList users={users} />}
    </Container>
  );
};

export default HomePage;
