import React, { useState } from 'react';
import { GitHubUser, GitHubRepo, getUserRepos } from '../services/githubApi';
import { useInfiniteQuery } from '@tanstack/react-query';
import RepoList from './RepoList';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import ListItemText from '@mui/material/ListItemText';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

const PER_PAGE = 10;

interface UserItemProps {
  user: GitHubUser;
}

const UserItem: React.FC<UserItemProps> = ({ user }) => {
  const [expanded, setExpanded] = useState<boolean>(false);
  const [displayedPages, setDisplayedPages] = useState<number>(1);

  // Eager load repos untuk user
  const {
    data: repoData,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    isLoading,
    isError,
    error,
  } = useInfiniteQuery<GitHubRepo[], Error>({
    queryKey: ['userRepos', user.login],
    initialPageParam: 1,
    queryFn: ({ pageParam = 1 }) => getUserRepos(user.login, pageParam, PER_PAGE),
    enabled: true, // eager load: fetch langsung
    getNextPageParam: (lastPage, allPages) => {
      return lastPage.length === PER_PAGE ? allPages.length + 1 : undefined;
    },
    staleTime: 1000 * 60 * 5,
  });

  // repos dari infinite query (per halaman)
  const repos = repoData ? repoData.pages : [];
  // Ambil repos yang akan ditampilkan sesuai jumlah halaman (displayedPages)
  const displayedRepos: GitHubRepo[] = repos.slice(0, displayedPages).flat();

  const handleToggleExpand = () => {
    setExpanded((prev) => !prev);
  };

  const handleLoadMore = async () => {
    // Jika semua halaman yang telah di-load sudah ditampilkan dan masih ada halaman selanjutnya
    if (repoData && displayedPages === repoData.pages.length && hasNextPage) {
      await fetchNextPage();
    }
    setDisplayedPages((prev) => prev + 1);
  };

  const handleShowLess = () => {
    setDisplayedPages(1);
  };

  return (
    <>
      <ListItem disablePadding>
        <ListItemButton onClick={handleToggleExpand}>
          <ListItemAvatar>
            <Avatar alt={user.login} src={user.avatar_url} />
          </ListItemAvatar>
          <ListItemText primary={user.login} />
          {expanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
        </ListItemButton>
      </ListItem>
      {expanded && (
        <div style={{ marginLeft: 48 }}>
          {isLoading && <Typography>Loading repos...</Typography>}
          {isError && <Typography color="error">{(error as Error).message}</Typography>}
          {!isLoading && !isError && (
            <>
              <RepoList
                repos={displayedRepos}
                username={user.login}
                onLoadMore={handleLoadMore}
                // Tampilkan tombol "Load More" jika masih ada halaman yang belum ditampilkan
                hasMore={hasNextPage || (repoData ? displayedPages < repoData.pages.length : false)}
                isLoadingMore={isFetchingNextPage}
              />
              {displayedPages > 1 && (
                <Button variant="text" onClick={handleShowLess} sx={{ marginTop: 1 }}>
                  Show Less
                </Button>
              )}
            </>
          )}
        </div>
      )}
    </>
  );
};

export default UserItem;
