import React from 'react';
import { GitHubRepo } from '../services/githubApi';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';

interface RepoListProps {
  repos: GitHubRepo[];
  username: string;
  onLoadMore: () => void;
  hasMore: boolean;
  isLoadingMore: boolean;
}

const RepoList: React.FC<RepoListProps> = ({ repos, username, onLoadMore, hasMore, isLoadingMore }) => {
  return (
    <div>
      <Typography variant="h6" gutterBottom>
        Repositories for "{username}"
      </Typography>
      <List>
        {repos.map((repo) => (
          <React.Fragment key={repo.id}>
            <ListItem>
              <ListItemText
                primary={
                  <a
                    href={repo.html_url}
                    target="_blank"
                    rel="noreferrer"
                    style={{ textDecoration: 'none', color: '#1976d2' }}
                  >
                    {repo.name}
                  </a>
                }
                secondary={`⭐ ${repo.stargazers_count}`}
              />
            </ListItem>
            <Divider />
          </React.Fragment>
        ))}
      </List>
      {hasMore && (
        <Button variant="contained" onClick={onLoadMore} disabled={isLoadingMore} sx={{ marginTop: 2 }}>
          {isLoadingMore ? 'Loading...' : 'Load More'}
        </Button>
      )}
    </div>
  );
};

export default RepoList;
