import React, { useState } from 'react';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import CircularProgress from '@mui/material/CircularProgress';
import Alert from '@mui/material/Alert';
import Button from '@mui/material/Button';

import SearchBar from '../components/SearchBar';
import UserList from '../components/UserList';
import { GitHubUser, searchUsers } from '../services/githubApi';
import { useQuery } from '@tanstack/react-query';

const HomePage: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [submittedQuery, setSubmittedQuery] = useState('');

  // Query untuk mencari user
  const {
    data: users = [],
    isLoading,
    error,
    refetch,
  } = useQuery<GitHubUser[], Error>({
    queryKey: ['searchUsers', submittedQuery],
    queryFn: () => searchUsers(submittedQuery),
    enabled: false, // default non-aktif, kita manual fetch
    staleTime: 1000 * 60 * 5,
  });

  // Ketika klik "Search"
  const handleSearch = async () => {
    if (!searchQuery.trim()) return;
    setSubmittedQuery(searchQuery.trim());
    await refetch(); // panggil fetch
  };

  return (
    <Container maxWidth="sm" sx={{ paddingY: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        GitHub User Search
      </Typography>

      {/* Form pencarian */}
      <SearchBar searchQuery={searchQuery} onQueryChange={setSearchQuery} />
      <Button variant="contained" onClick={handleSearch} fullWidth>
        Search
      </Button>

      {/* Loading & Error */}
      {isLoading && <CircularProgress sx={{ display: 'block', margin: '20px auto' }} />}
      {error && <Alert severity="error">{error.message}</Alert>}

      {/* Tampilkan daftar user */}
      {users.length > 0 && <UserList users={users} />}
    </Container>
  );
};

export default HomePage;
