import React, { useState } from 'react';
import { GitHubUser, GitHubRepo, getUserRepos } from '../services/githubApi';
import { useInfiniteQuery } from '@tanstack/react-query';
import RepoList from './RepoList';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import ListItemText from '@mui/material/ListItemText';
import Typography from '@mui/material/Typography';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

const PER_PAGE = 10;

interface UserListProps {
  users: GitHubUser[];
}

const UserList: React.FC<UserListProps> = ({ users }) => {
  return (
    <div>
      <Typography variant="h6" gutterBottom>
        Showing users
      </Typography>
      {users.length === 0 && (
        <Typography variant="body2" color="textSecondary">
          No users found.
        </Typography>
      )}
      <List>
        {users.map((user) => (
          <UserItem key={user.id} user={user} />
        ))}
      </List>
    </div>
  );
};

interface UserItemProps {
  user: GitHubUser;
}

/**
 * Komponen untuk 1 user (dropdown expand/collapse).
 * Repos di-fetch "eager" dengan useInfiniteQuery enabled=true.
 */
const UserItem: React.FC<UserItemProps> = ({ user }) => {
  const [expanded, setExpanded] = useState<boolean>(false);

  // Eager load repos
  const {
    data: repoData,
    fetchNextPage,
    hasNextPage,
    isFetchingNextPage,
    isLoading,
    isError,
    error,
  } = useInfiniteQuery<GitHubRepo[], Error, { pages: GitHubRepo[]; pageParams: number[] }, any, number>({
    queryKey: ['userRepos', user.login],
    initialPageParam: 1,
    queryFn: ({ pageParam = 1 }) => getUserRepos(user.login, pageParam, PER_PAGE),
    enabled: true, // fetch segera (eager load)
    getNextPageParam: (lastPage, allPages) => {
      return lastPage.length === PER_PAGE ? allPages.length + 1 : undefined;
    },
    staleTime: 1000 * 60 * 5,
  });

  // Kumpulkan semua repos
  const repos: GitHubRepo[] = repoData ? repoData.pages.flat() : [];

  // Klik user -> expand/collapse
  const handleToggleExpand = () => {
    setExpanded((prev) => !prev);
  };

  return (
    <>
      <ListItem disablePadding>
        <ListItemButton onClick={handleToggleExpand}>
          <ListItemAvatar>
            <Avatar alt={user.login} src={user.avatar_url} />
          </ListItemAvatar>
          <ListItemText primary={user.login} />
          {expanded ? <ExpandLessIcon /> : <ExpandMoreIcon />}
        </ListItemButton>
      </ListItem>
      {expanded && (
        <div style={{ marginLeft: 48 /* agar sedikit menjorok */ }}>
          {isLoading && <Typography>Loading repos...</Typography>}
          {isError && <Typography color="error">{(error as Error).message}</Typography>}
          {!isLoading && !isError && (
            <RepoList
              repos={repos}
              username={user.login}
              onLoadMore={fetchNextPage}
              hasMore={!!hasNextPage}
              isLoadingMore={isFetchingNextPage}
            />
          )}
        </div>
      )}
    </>
  );
};

export default UserList;
