import React from 'react';
import { GitHubRepo } from '../services/githubApi';
import { Card, CardContent, CardActions, Typography, Button, Grid, Link, Box, Paper } from '@mui/material';

interface RepoListProps {
  repos: GitHubRepo[];
  username: string;
  onLoadMore: () => void;
  hasMore: boolean;
  isLoadingMore: boolean;
}

const RepoList: React.FC<RepoListProps> = ({
  repos,
  username,
  onLoadMore,
  hasMore,
  isLoadingMore,
}) => {
  return (
    <Box sx={{ mt: 3 }}>
      <Typography variant="h6" sx={{ fontWeight: 'bold', mb: 2 }}>
        Repositories for "{username}"
      </Typography>

      {repos.length === 0 ? (
        <Paper sx={{ p: 3, mt: 2, bgcolor: 'transparent', boxShadow: 0, border: 0 }}>
            <Typography variant="subtitle1" color="text.secondary">
                No repositories available.
            </Typography>
        </Paper>      
      ) : (
        <Grid container spacing={2}>
          {repos.map((repo) => (
            <Grid item xs={12} sm={6} md={4} key={repo.id}>
              <Card
                sx={{
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column',
                  boxShadow: 3,
                  borderRadius: 2,
                }}
              >
                <CardContent sx={{ overflow: 'hidden' }}>
                  <Typography
                    variant="h6"
                    component="div"
                    sx={{
                      fontWeight: 600,
                      wordWrap: 'break-word',
                      whiteSpace: 'normal',
                      display: '-webkit-box',
                      WebkitLineClamp: 1,
                      WebkitBoxOrient: 'vertical',
                      overflow: 'hidden',
                    }}
                  >
                    <Link
                      href={repo.html_url}
                      target="_blank"
                      underline="none"
                      color="primary"
                      sx={{ fontWeight: 600 }}
                    >
                      {repo.name}
                    </Link>
                  </Typography>
                  <Typography
                    variant="body2"
                    color="text.secondary"
                    sx={{
                      mt: 1,
                      display: '-webkit-box',
                      WebkitLineClamp: 2,
                      WebkitBoxOrient: 'vertical',
                      overflow: 'hidden',
                    }}
                  >
                    {repo.description || 'No description'}
                  </Typography>
                </CardContent>
                <CardActions sx={{ mt: 'auto', px: 2, pb: 2 }}>
                  <Typography variant="body2" color="text.secondary">
                    ‚≠ê {repo.stargazers_count}
                  </Typography>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

        {hasMore && (
            <Box sx={{ mt: 5, textAlign: 'center' }}>
                <Button
                variant="contained"
                onClick={onLoadMore}
                disabled={isLoadingMore}
                sx={{ textTransform: 'none' }}
                >
                {isLoadingMore ? 'Loading...' : 'Load More'}
                </Button>
            </Box>
        )}

    </Box>
  );
};

export default RepoList;
